#version 450 coreout vec4 FragColour;//view spacein vec3 Normal;in vec3 FragPos;in vec3 LightPos;uniform vec3 objectColour;uniform vec3 lightColour;void main(){	// Ambient	float ambientStrength = 0.1;	vec3 ambient = ambientStrength * lightColour;	// Diffuse	vec3 norm = normalize(Normal);    vec3 lightDir = normalize(LightPos - FragPos);    float diff = max(dot(norm, lightDir), 0.0); //ensures non negative    vec3 diffuse = diff * lightColour;	//Specular	float specularStrength = 0.8;	int shininess = 32; //the higher the shininess the more direct reflection is, i.e. a smaller highlight	vec3 viewDir = normalize( - FragPos); // since view is 0,0,0 this is the same as subtracting the view by position	vec3 reflectDir = reflect(-lightDir, norm);// we negate the lightDir so the vector points from the light to the fragment	float spec = pow(max(dot(viewDir, reflectDir), 0.0), shininess);	vec3 specular = specularStrength * spec * lightColour;	vec3 result = (ambient + diffuse + specular) * objectColour;	FragColour = vec4(result, 1.0);}